import React from 'react';
import { Link, BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Game from './game';
import styled from 'styled-components';


function Single() {

    return (
        <Page>
            <Title>Single Player</Title>
            <Form action='/action_page.php' target='_blank'>
                <label>Name:<FormItem type='text' id='name'/></label>
                <label>User speed (1-10):<FormItem type='number' id='uSpeed'/></label>
                <label>PC speed (1-10):<FormItem type='number' id='pcSpeed'/></label>
            </Form>
            <StyledLink to="/game">Play</StyledLink> 
        </Page>
    );
}

export default () => {
    return (
      <Router>
        <Switch>
          <Route exact path="/game" component={Game} />
          <Route exact path="/single" component={Single} />
        </Switch>
      </Router>
    );
  };


const Page = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  text-align: center;
`;
const Title = styled.h1`
  font-size: calc(5px + 2vmin);
`
const Form = styled.form`
  display: flex;
  border: 2px solid pink;
  flex-direction: column;
  justify-content: center;
  text-align: left;
  padding:50px;
  font-size: calc(5px + 2vmin);
  direction: ltr;
`;
const StyledLink = styled(Link)`
  color: mintcream;
  text-decoration: none;
`;

const FormItem = styled.input`
  border: none;
  padding: 3px;
  color: white;
  background-color: #282c34;

`;
